package package1;

public class pgm36 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=0;
		if(n==0||n==1) {
			System.out.println("the number is not a prime number");
		}
		else if(n==2) {
			System.out.println("the number is prime number");
		}
		else if(n%2==0) {
			System.out.println("the number is not a prime number");
		}
		else
		{
			System.out.println("the number is prime number");
		}

	}

}
