package package1;

public class pgm39 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c;
		for(a=4;a>=0;a--) {
			for(b=4;b>=a;b--) {
				System.out.print(" ");
			}
			for(c=0;c<=a;c++) {
				System.out.print("*");
			}
			System.out.println();
		}

	}

}





