package package1;

public class pgm40 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c;
		for(a=0;a<3;a++) {
			for(b=0;b<=a;b++) {
				System.out.print("  ");
			}
			for(c=0;c<=a;c++) {
				System.out.print("*");
			}
			System.out.println();		
			
		}

	}

}
